mapper.update:
   		UPDATE 
   			TB_EEP_BRD_M
		SET
			 BRD_TTL        	= 	#{brdTtl}
			,BRD_CNTNT     		= 	#{brdCntnt}
			,BRD_UPDT_DT      	= 	NOW()
			,USE_YN 			= 	#{useYn}
			,DEL_YN 			= 	#{delYn}
   		WHERE
         	1=1 AND BRD_TYP_CD = 'NT' AND BRD_SQ = #{brdSq}

   		UPDATE 
   			TB_EEP_BRD_M
		SET
			 BRD_TTL        	= 	#{brdTtl}
			,BRD_CNTNT     		= 	#{brdCntnt}
			,BRD_TCHR 			= 	#{brdTchr}
			,BRD_EDU_DT 		= 	#{brdEduDt}
			,BRD_UPDT_DT      	= 	NOW()
			,USE_YN 			= 	#{useYn}
			,DEL_YN 			= 	#{delYn}
   		WHERE
         	1=1 AND BRD_TYP_CD = 'ED' AND BRD_SQ = #{brdSq}

mapper.delete:

UPDATE TB_EEP_BRD_M
   SET BRD_RE_ORD = BRD_RE_ORD + 1
 WHERE BRD_RE_REF = #{brdReRep} 
   AND BRD_RE_ORD > #{brdReOrd}

mapper.update:
